<?php

namespace AppBundle\Repository;

/**
 * TDRSpecificRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TDRSpecificRepository extends \Doctrine\ORM\EntityRepository
{
    public function listAll()
    {
        $qb = $this->createQueryBuilder('tdr_s')
            ->getQuery();

        return $qb->getResult();
    }

    public function listAllByProjet($id_projet)
    {
        $qb = $this->createQueryBuilder('tdr_s')
            ->join('tdr_s.projet', 'projet')
            ->where('projet.id = :id')
            ->setParameter('id', $id_projet)
            ->getQuery();

        return $qb->getResult();
    }
}
